package leetcode.common.twopoint;

import com.sun.org.apache.regexp.internal.RE;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;
import java.util.Objects;
import java.util.stream.Collectors;
import java.util.stream.Stream;

/**
 * 给定一个字符串s和一个非空字符串p，找到s中所有是p的字母异位词的子串，返回这些子串的起始索引。
 *
 * 字符串只包含小写英文字母，并且字符串s和 p的长度都不超过 20100。
 *
 * 说明：
 *
 * 字母异位词指字母相同，但排列不同的字符串。
 * 不考虑答案输出的顺序。
 *
 */
public class Test438 {

    public List<Integer> findAnagrams(String s, String p) {
        List<Integer> list = new ArrayList<>();
        if (s == null) return list;
        int len = s.length();
        if (len == 0 || p == null || p.length() > len) return list;
        int left = 0;
        int right = p.length() - 1;
        char[] chars = s.toCharArray();
        while(right < len){
            if (check(left,right,chars,p)){
                list.add(left);
            }
            right++;
            left++;
        }
        return list;
    }






    private boolean check(int left,int right,char[] chars, String p){
        char[] chars1 = Arrays.copyOfRange(chars, left, right + 1);
        Arrays.sort(chars1);
        String collect1 = Stream.of(chars1).map(String::new).collect(Collectors.joining(""));
        char[] chars2 = p.toCharArray();
        Arrays.sort(chars2);
        String collect2 = Stream.of(chars2).map(String::new).collect(Collectors.joining(""));
        return collect1.equals(collect2);
    }


    public static void main(String[] args) {
        Test438 test438 = new Test438();
        test438.findAnagrams("","yqrbgjdwtcaxzsnifvhmou");
    }


}
